import {
  HeartFilled,
  HeartOutlined,
  ShoppingCartOutlined,
} from "@ant-design/icons";
import { message, Modal } from "antd";
import React, { useState } from "react";
import { connect } from "react-redux";

/* custom components */
import {
  CartButton,
  WishlistButton,
} from "../../page/ProductDetail/ProductDetailStyle";
import { Container, Description } from "./ProductStyle";

/* others */
import cnf from "../../config";
import { getCartId, getToken } from "../../utils";

// Actions
import { saveWishlistDetail } from "../../action/saveWishlistAction";
import { updateCart, updateCartReset } from "../../action/updateCartAction";

// components
import AccountDrawer from "../../component/AccountDrawer/AccountDrawer";

const Product = (props) => {
  // variables
  const {
    product,
    currencyType,
    updateCart,
    updateCartReset,
    updateCartState,

    saveWishlistDetail,
    saveWishlistState,
  } = props;

  const [accountVisible, setAccountVisible] = useState(false);

  const handleAddToCart = (product_id, qty) => {
    updateCart({
      product_id: product_id,
      qty: qty,
      cart_id: getCartId(),
    });
  };

  const handleWishlistAdd = (productId) => {
    if (product?.is_wishlisted) {
      message.warning("The product already wishlisted");
    } else {
      if (!getToken()) {
        setAccountVisible(true);
        return;
      }
      saveWishlistDetail({
        product_id: productId,
      });
    }
  };
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <Container>
      <div
        style={{
          position: "relative",
          background: "#fff",
          border: "solid 1px #cccccca8",
        }}
      >
        <a href={`/p/${product?.url_key}`} target="_blank">
          <div className="desktop_image">
            <img
              src={`${product ? cnf.s3_base_url : ""}${
                (product && product.thumbnail) || "/saree1.jpeg"
              }`}
              alt=""
              style={{ width: "100%", height: "auto" }}
            />
          </div>
          <Description>
            <div
              style={{
                fontSize: 16,
                color: "#000",
                textOverflow: "ellipsis",
                whiteSpace: "nowrap",
                overflow: "hidden",
              }}
            >
              {(product && product.name) || "product name"}
            </div>
            <div
              style={{
                fontWeight: "bold",
                color: "#000",
                fontSize: 16,
                textAlign: "center",
              }}
            >
              {currencyType == "USD" ? <>$</> : <>₹</>}
              {product?.actual_price}
              {product?.is_sale_price === "yes" && (
                <span
                  style={{
                    fontWeight: "normal",
                    color: "#00000099",
                    fontSize: 14,
                    textAlign: "center",
                    textDecoration: "line-through",
                    marginLeft: 4,
                  }}
                >
                  {currencyType == "USD" ? <>$</> : <>₹</>}
                  {product.regular_price}
                </span>
              )}
            </div>
          </Description>
          {product?.is_sale_price === "yes" && (
            <div style={{ position: "absolute", top: 10, left: 10 }}>
              <div
                style={{
                  background: "green",
                  color: "#fff",
                  padding: "0px 5px",
                  borderRadius: 3,
                }}
              >
                {product.discount}% OFF
              </div>
            </div>
          )}
        </a>
        <div style={{ display: "flex", flexDirection: "row" }}>
          {product?.stock === 0 ? (
            <CartButton icon={<ShoppingCartOutlined />} disabled>
              OUT OF STOCK
            </CartButton>
          ) : (
            <CartButton
              onClick={showModal}
              icon={<ShoppingCartOutlined />}
              // onClick={() => handleAddToCart(product?.id, 1)}
              loading={updateCartState.apiState === "loading"}
            >
              ADD TO CART
            </CartButton>
          )}
          {props.forWishlist ? (
            <></>
          ) : (
            <WishlistButton
              style={{ height: "auto" }}
              type="default"
              size="large"
              icon={
                product?.is_wishlisted ||
                (saveWishlistState.apiState === "success" &&
                  saveWishlistState.product_id === product?.id) ? (
                  <HeartFilled />
                ) : (
                  <HeartOutlined />
                )
              }
              onClick={() => handleWishlistAdd(product?.id)}
            ></WishlistButton>
          )}
        </div>
      </div>

      {/* Currently this function not build  so its commented */}
      {/* <div className="action-btns" >
                <div style={{ width: "20%" }} >
                    <CartBtn icon={<HeartOutlined style={{ fontSize: 20, color: "red" }} />}
                    />
                </div>
                <div style={{ width: "80%" }} >
                    <CartBtn type="primary" onClick={() => console.log("hi")} >ADD TO CART</CartBtn>
                </div>
            </div> */}

      {/* {
                isWishlist === true &&
                <div style={{ width: "100%" }} >
                    <CartBtn type="primary" onClick={() => handleRemove(product.wishlist_id)} >Remove</CartBtn>
                </div>
            } */}
      <AccountDrawer
        accountVisible={accountVisible}
        onClose={() => setAccountVisible(false)}
      />
      <Modal
        title="Select Size"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <div className="product-details">
          <div className="row" style={{ display: "flex" }}>
            <div className="col-md-3" style={{ width: "30%" }}>
              <div className="product-image">
                <img
                  src={`${product ? cnf.s3_base_url : ""}${
                    (product && product.thumbnail) || "/saree1.jpeg"
                  }`}
                  alt=""
                  style={{ width: "100%", height: "auto" }}
                />
              </div>
            </div>
            <div className="col-md-9" style={{ width: "70%" }}>
              <div className="d-content">
                <Description>
                  <div
                    style={{
                      fontSize: 16,
                      color: "#000",
                      textOverflow: "ellipsis",
                      whiteSpace: "nowrap",
                      overflow: "hidden",
                    }}
                  >
                    {(product && product.name) || "product name"}
                  </div>
                  <div
                    style={{
                      fontWeight: "bold",
                      color: "#000",
                      fontSize: 16,
                      textAlign: "center",
                    }}
                  >
                    {currencyType == "USD" ? <>$</> : <>₹</>}
                    {product?.actual_price}
                    {product?.is_sale_price === "yes" && (
                      <span
                        style={{
                          fontWeight: "normal",
                          color: "#00000099",
                          fontSize: 14,
                          textAlign: "center",
                          textDecoration: "line-through",
                          marginLeft: 4,
                        }}
                      >
                        {currencyType == "USD" ? <>$</> : <>₹</>}
                        {product.regular_price}
                      </span>
                    )}
                  </div>
                </Description>
              </div>
            </div>
          </div>
        </div>
        <div className="size-btn">
          <div className="main-btn">
            <a href="#" className="btn btn-group small">
              S
            </a>
            <a href="#" className="btn btn-group medium">
              M
            </a>
            <a href="#" className="btn btn-group large">
              L
            </a>
            <a href="#" className="btn btn-group x-large">
              XL
            </a>
          </div>
        </div>
      </Modal>
    </Container>
  );
};
// export default Product

const mapStateToProps = (state) => ({
  updateCartState: state.updateCart,
  saveWishlistState: state.saveWishlist,
});
const mapDispatchToProps = (dispatch) => ({
  updateCart: (params) => dispatch(updateCart(params)),
  updateCartReset: () => dispatch(updateCartReset()),
  saveWishlistDetail: (params) => dispatch(saveWishlistDetail(params)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Product);
